mapper.update:
update board_list 
   set views = views+1 
 where board_no=#{boardNo}

mapper.select:
select refer,depth,step 
from board_list
where board_no=#{boardNo}

select ifnull(min(step),0) step
from board_list
where refer=#{refer} and step > #{step} and depth <= #{depth}

select max(step)+1 maxStep
from board_list
where refer=#{refer}

mapper.update:
update board_list
set step=step+1
where refer=#{refer} and step >= #{step}

update board_list
   set board_no=#{boardNo}, title=#{title}, content=#{content}
 where board_no=#{boardNo}
